// Use a regex to extract the hostname.
// The regex breakdown:
// ^(?:https?:\/\/)?  --> Optionally match the protocol (http:// or https://)
// (?:www\.)?         --> Optionally match "www."
// ([^\/]+)           --> Capture all characters until the first "/" (the hostname)
const match = $input.item.json.url.match(/^(?:https?:\/\/)?(?:www\.)?([^\/]+)/i);

if (!match) {
  throw new Error("Unable to regex match url")
}

const domain = match[1];
let sourceName = domainSourceMap[domain];

if (!sourceName) {
  // Fallback: generate a friendly name by removing any leading "www.", 
  // then removing the TLD (the last dot segment),
  // and finally replacing any remaining dots with dashes.
  sourceName = domain
    .replace(/^www\./, '')
    .replace(/\.[^.]+$/, '') // removes the TLD
    .replace(/\./g, '-');
}

if (!sourceName) {
    throw new Error(`Unknown source detected: ${domain}`);
}

return {
  sourceName: sourceName,
  title: $input.item.json.title,
  creator: $input.item.json.authors[0]?.name,
  link: $input.item.json.url,
  pubDate: $input.item.json.date_published,
  isoDate: $input.item.json.date_published,
  feedType: "article",
  feedUrl: "https://rss.app/feeds/v1.1/AkOariu1C7YyUUMv.json"
}

assignments.value:
={{ 
  $json.isoDate.substring(0, 10) + "/" + 
  $json.title
    .toLowerCase()
    .replace(/[^a-z0-9 -]/g, '')  // remove characters outside a-z, 0-9, space, and dash
    .trim()                      // remove any leading/trailing whitespace
    .replace(/\s+/g, '-')        // replace whitespace with dash
    .replace(/-+/g, '-')         // collapse multiple dashes
  + '.' + $json.sourceName 
}}

