// Get all input items (these are just file IDs)
const allItems = $input.all();

console.log(`Received ${allItems.length} file IDs from Google Drive`);

if (allItems.length === 0) {
    throw new Error("No file IDs received from Google Drive");
}

// Since we know these are all video files from the B-roll folder,
// we can directly select a random one
const randomIndex = Math.floor(Math.random() * allItems.length);
const selectedVideoId = allItems[randomIndex].json.id;

console.log(`Selected video ID: ${selectedVideoId}`);

// Build the video URL using the file ID
const videoUrl = `https://drive.google.com/uc?export=download&id=${selectedVideoId}`;

// Get the uploaded image URL from the previous node
let quoteImageUrl = '';
try {
    const uploadedImageData = $node["Google Drive: Upload Quote Image"].json;
    quoteImageUrl = uploadedImageData.webContentLink || `https://drive.google.com/uc?export=download&id=${uploadedImageData.id}`;
} catch (e) {
    throw new Error("Could not find uploaded image URL: " + e.message);
}

// Get other required data for the final Airtable update
const airtableData = $node["Airtable"].json;
const webhookData = $node["Webhook"].json;

console.log(`Video URL: ${videoUrl}`);
console.log(`Image URL: ${quoteImageUrl}`);

// Return the FFmpeg payload
return [{
    json: {
        ffmpeg_json: {
            id: "quote-overlay-video",
            inputs: [
                {
                    file_url: videoUrl,
                    options: [{ option: "-t", argument: 7 }]
                },
                {
                    file_url: quoteImageUrl
                }
            ],
            filters: [
                { filter: "[0:v]scale=1080:1920:force_original_aspect_ratio=decrease,pad=1080:1920:(ow-iw)/2:(oh-ih)/2:color=black[bg]" },
                { filter: "[1:v]scale=1080:1080[quote]" },
                { filter: "[bg][quote]overlay=(W-w)/2:(H-h)/2[outv]" }
            ],
            outputs: [
                {
                    options: [
                        { option: "-map", argument: "[outv]" },
                        { option: "-c:v", argument: "libx264" },
                        { option: "-preset", argument: "fast" },
                        { option: "-crf", argument: 23 },
                        { option: "-t", argument: 7 }
                    ]
                }
            ],
            metadata: {
                thumbnail: true,
                filesize: true,
                duration: true,
                bitrate: true,
                encoder: true
            }
        },
        // Include data for the final Airtable update
        baseId: webhookData.body.variables.BASE_ID,
        contentTable: webhookData.body.variables.CONTENT_TABLE,
        recordIdToUpdate: airtableData.id
    }
}];

// The AI returns a single item with a raw text string in the 'output' field.
// First, get the raw text output from the first input item.
const aiOutput = $input.first().json.output;

// --- FINAL, ROBUST PARSER ---
const clips = [];
// This new regex handles both "Clip1:" and "**Clip 1:**" formats
const clipRegex = /(?:\*\*)?Clip\s*(\d+):\s*Description:\s*(.*?)\s*Clip Contents:\s*([\s\S]*?)(?=\s*(?:\*\*)?Clip\s*\d+:|$)/g;

let clipMatch;
while ((clipMatch = clipRegex.exec(aiOutput)) !== null) {
    clips.push({
        clip_number: parseInt(clipMatch[1].trim()),
        description: clipMatch[2].trim(),
        full_transcript: clipMatch[3].trim(),
    });
}

// Get the SRT content from the 'Download SRT' node.
const srtContent = $('Download SRT').item.json.data;

// Function to convert SRT time string (HH:MM:SS,ms) to seconds.
const timeToSeconds = (time) => {
    const parts = time.replace(',', '.').split(':');
    return parseFloat(parts[0]) * 3600 + parseFloat(parts[1]) * 60 + parseFloat(parts[2]);
};

// Parse the entire SRT file into blocks with timing and text.
const srtBlocks = [];
const srtRegex = /(\d+)\s*\n\s*(\d{2}:\d{2}:\d{2}[,.]\d{3})\s*-->\s*(\d{2}:\d{2}:\d{2}[,.]\d{3})\s*\n([\s\S]*?)(?=\n\n|\n$|$)/g;
let match;
while ((match = srtRegex.exec(srtContent)) !== null) {
    const startSeconds = timeToSeconds(match[2]);
    const endSeconds = timeToSeconds(match[3]);
    srtBlocks.push({
        num: parseInt(match[1]),
        startTime: match[2],
        endTime: match[3],
        startSeconds: startSeconds,
        endSeconds: endSeconds,
        duration: endSeconds - startSeconds,
        text: match[4].trim()
    });
}

// Function to clean text for matching (remove speaker labels, punctuation, etc.).
const cleanForMatching = (text) => {
    return text
        .replace(/^[^:]*:\s*/gm, '') // Remove speaker labels like "Jocelyn: "
        .replace(/[^a-zA-Z0-9\s]/g, ' ') // Replace punctuation with spaces
        .replace(/\s+/g, ' ') // Normalize whitespace
        .toLowerCase()
        .trim();
};

const processedClips = [];
const fullSrtText = srtBlocks.map(b => cleanForMatching(b.text)).join(' ');

// Helper function for fuzzy matching
const findBestMatchIndex = (wordsToSearch, textToSearchIn, minLength = 5) => {
    for (let i = Math.min(wordsToSearch.length, 10); i >= minLength; i--) {
        const phrase = wordsToSearch.slice(0, i).join(' ');
        if (!phrase) continue;
        const index = textToSearchIn.indexOf(phrase);
        if (index !== -1) {
            return { index, phrase };
        }
    }
    return { index: -1, phrase: null };
};


// Loop through each clip identified by the AI.
for (const clip of clips) {
    const clipTranscript = clip.full_transcript || '';
    if (!clipTranscript) {
        continue;
    }

    const cleanClipTranscript = cleanForMatching(clipTranscript);
    const clipWords = cleanClipTranscript.split(' ');

    // 1. Find the start of the clip
    const { index: startIndexInSrt, phrase: startMatchPhrase } = findBestMatchIndex(clipWords, fullSrtText);

    if (startIndexInSrt === -1) {
        continue;
    }

    // 2. Find the end of the clip
    const endClipWords = clipWords.slice(-10);
    const searchSpaceForEnd = fullSrtText.substring(startIndexInSrt + startMatchPhrase.length);
    const { index: endIndexRelative, phrase: endMatchPhrase } = findBestMatchIndex(endClipWords, searchSpaceForEnd);

    let endIndexInSrt;
    if (endIndexRelative !== -1) {
        endIndexInSrt = startIndexInSrt + startMatchPhrase.length + endIndexRelative + endMatchPhrase.length;
    } else {
        endIndexInSrt = startIndexInSrt + cleanClipTranscript.length;
    }

    let accumulatedChars = 0, startBlock = null, endBlock = null, charOffsetStart = 0, charOffsetEnd = 0;
    for (let i = 0; i < srtBlocks.length; i++) {
        const block = srtBlocks[i];
        const blockTextClean = cleanForMatching(block.text);
        const blockLength = blockTextClean.length + 1;
        if (startBlock === null && (accumulatedChars + blockLength) > startIndexInSrt) {
            startBlock = block;
            charOffsetStart = startIndexInSrt - accumulatedChars;
        }
        if (endBlock === null && (accumulatedChars + blockLength) > endIndexInSrt) {
            endBlock = block;
            charOffsetEnd = endIndexInSrt - accumulatedChars;
        }
        accumulatedChars += blockLength;
        if (startBlock && endBlock) break;
    }

    if (!startBlock || !endBlock || endBlock.num < startBlock.num) {
        continue;
    }

    const startBlockTextClean = cleanForMatching(startBlock.text);
    const startRatio = Math.max(0, Math.min(1, charOffsetStart / (startBlockTextClean.length || 1)));
    const accurateStartTime = startBlock.startSeconds + (startBlock.duration * startRatio);

    const endBlockTextClean = cleanForMatching(endBlock.text);
    const endRatio = Math.max(0, Math.min(1, charOffsetEnd / (endBlockTextClean.length || 1)));
    const accurateEndTime = endBlock.startSeconds + (endBlock.duration * endRatio);

    const startIndex = srtBlocks.findIndex(b => b.num === startBlock.num);
    const endIndex = srtBlocks.findIndex(b => b.num === endBlock.num);
    const relevantBlocks = srtBlocks.slice(startIndex, endIndex + 1);

    const srtSegment = relevantBlocks.map(b => 
        `${b.num}\n${b.startTime} --> ${b.endTime}\n${b.text}`
    ).join('\n\n');

    processedClips.push({
        json: {
            ...clip,
            startTime: accurateStartTime,
            endTime: accurateEndTime,
            srt_segment: srtSegment,
            startBlock: startBlock.num,
            endBlock: endBlock.num,
            blockCount: relevantBlocks.length,
        }
    });
}

return processedClips;

// Updated Build Cut Command Code to work with Airtable clip records
// Initialize an array to hold the output for each clip
const allPayloads = [];

// Get webhook data - try multiple sources
let webhookData;
try {
  // Try to get from Variables node first
  webhookData = $('Variables').item.json.webhook_body;
} catch (error) {
  try {
    // Fallback to Switch node
    webhookData = $('Switch').item.json.webhook_body;
  } catch (error2) {
    try {
      // Fallback to Webhook node directly
      webhookData = $('Webhook').item.json.body;
    } catch (error3) {
      console.error("Could not access webhook data from any source");
      return [];
    }
  }
}

console.log("Webhook data found:", !!webhookData);

// Get BASE_ID for metadata
const baseId = webhookData?.variables?.BASE_ID || 'unknown';

// Get callback URL - try multiple paths
let callbackUrl;
try {
  callbackUrl = webhookData?.action?.___CALLBACK_UPDATE_WEBHOOK_URL;
  if (!callbackUrl) {
    // Try alternative path
    callbackUrl = webhookData?.action?.CALLBACK_UPDATE_WEBHOOK_URL;
  }
  if (!callbackUrl) {
    console.log("Warning: No callback URL found, using placeholder");
    callbackUrl = "https://placeholder-webhook-url.com";
  }
} catch (error) {
  console.log("Error getting callback URL:", error.message);
  callbackUrl = "https://placeholder-webhook-url.com";
}

console.log("Processing", $input.all().length, "clips");

// Loop through every incoming item (clip records)
for (const item of $input.all()) {
  try {
    // Get the clip record data
    const clipRecord = item.json;

    console.log("Processing clip:", clipRecord.id, clipRecord['Clip Description']);

    /*****************************************************************
     * A ‚ñ∏ Extract essential data FOR THIS CLIP                      *
     *****************************************************************/
    // Get video URL - handle array format from Airtable
    const videoUrlArray = clipRecord['Video URL (from Videos)'];
    const videoUrl = Array.isArray(videoUrlArray) ? videoUrlArray[0] : videoUrlArray;

    const startTimeFromAirtable = clipRecord['Start Time'];
    const endTimeFromAirtable = clipRecord['End Time'];
    const clipTitle = clipRecord['Clip Description'];

    /*****************************************************************
     * B ‚ñ∏ Validate required fields FOR THIS CLIP                     *
     *****************************************************************/
    if (!videoUrl) {
      console.error(`Could not find "Video URL (from Videos)" in record ID ${clipRecord.id}`);
      continue;
    }
    if (startTimeFromAirtable === undefined || startTimeFromAirtable === null) {
      console.error(`The "Start Time" field is missing in record ID: ${clipRecord.id}`);
      continue;
    }
    if (endTimeFromAirtable === undefined || endTimeFromAirtable === null) {
      console.error(`The "End Time" field is missing in record ID: ${clipRecord.id}`);
      continue;
    }

    /*****************************************************************
     * C ‚ñ∏ Calculate final clip boundaries FOR THIS CLIP              *
     *****************************************************************/
    const startTime = Math.max(0, startTimeFromAirtable - 0.2);
    const endTime = endTimeFromAirtable + 0.2;
    const duration = endTime - startTime;

    if (duration <= 0) {
      console.error(`Invalid duration (${duration.toFixed(2)}s) for clip: "${clipTitle || clipRecord.id}"`);
      continue;
    }

    console.log(`Clip timing - Start: ${startTime.toFixed(2)}s, End: ${endTime.toFixed(2)}s, Duration: ${duration.toFixed(2)}s`);

    /*****************************************************************
     * D ‚ñ∏ Configure FFmpeg inputs FOR THIS CLIP                      *
     *****************************************************************/
    const inputs = [{
      file_url: videoUrl,
      options: [
        { option: '-ss', argument: startTime.toFixed(3) },
        { option: '-t', argument: duration.toFixed(3) }
      ]
    }];

    /*****************************************************************
     * E ‚ñ∏ Configure filters array FOR THIS CLIP                      *
     *****************************************************************/
    const filters = [];
    if (duration > 1) {
      filters.push({
        filter: "[0:v]fade=t=out:st=" + (duration - 0.5).toFixed(3) + ":d=0.5[v]"
      });
    }

    /*****************************************************************
     * F ‚ñ∏ Configure output mapping FOR THIS CLIP                     *
     *****************************************************************/
    const outputMap = [];
    if (filters.length > 0) {
      outputMap.push({ option: '-map', argument: '[v]' });
    } else {
      outputMap.push({ option: '-map', argument: '0:v' });
    }
    outputMap.push({ option: '-map', argument: '0:a' });
    outputMap.push(
      { option: '-c:v', argument: 'libx264' },
      { option: '-c:a', argument: 'aac' },
      { option: '-preset', argument: 'fast' },
      { option: '-crf', argument: '23' },
      { option: '-movflags', argument: '+faststart' }
    );

    /*****************************************************************
     * G ‚ñ∏ Assemble final payload FOR THIS CLIP                     *
     *****************************************************************/
    const ffmpegPayload = {
      id: clipRecord.id,
      inputs: inputs,
      filters: filters.length > 0 ? filters : undefined,
      outputs: [{ options: outputMap }],
      webhook_url: callbackUrl,
      metadata: {
        thumbnail: true,
        base_id: baseId,
        record_id: clipRecord.id,
        clip_id: clipRecord.id,
        start_time: startTime,
        end_time: endTime,
        clip_description: clipTitle
      }
    };

    // Add the completed payload for THIS CLIP to our results array
    allPayloads.push({
      json: {
        ffmpegPayload: ffmpegPayload,
        clipInfo: {
          id: clipRecord.id,
          description: clipTitle,
          duration: duration,
          startTime: startTime,
          endTime: endTime
        }
      }
    });

    console.log(`Successfully processed clip ${clipRecord.id}: ${clipTitle}`);

  } catch (error) {
    // If one item fails, log the error and continue
    console.error(`Error processing item ${item.json.id}:`, error.message);
    console.error("Stack:", error.stack);
  }
}

console.log(`Processed ${allPayloads.length} clips successfully`);

// Return the entire array of processed clips
return allPayloads;

