const { session_id, session_summary, ideas } = items[0].json.output;

return ideas.map(idea => ({
  json: {
    ...idea,
    session_id,
    session_summary
  }
}));

// Get inputs
const textContent = $input.first().json.content;
const webhookData = $input.first().json.webhook_body || $('Webhook').item.json.body;
const autoId = webhookData.action.AUTO_ID;

// Clean up the content and handle multiple separator types
let cleanedContent = textContent.trim();

try {
  // Split by both newlines AND vertical tab characters (\u000b)
  const lines = cleanedContent
    .split(/[\n\u000b]+/)  // Split on newlines OR vertical tabs (one or more)
    .filter(line => line.trim() !== '');

  const prompts = [];

  for (const line of lines) {
    try {
      const promptObj = JSON.parse(line.trim());
      prompts.push(promptObj);
    } catch (lineError) {
      console.log(`Skipped invalid JSON line: ${line.substring(0, 50)}...`);
    }
  }

  // Find the matching prompt by AUTO_ID
  const selectedPrompt = prompts.find(p => p.id == autoId);

  if (!selectedPrompt) {
    return [{ 
      json: { 
        error: `No prompt found with ID ${autoId}`,
        available_ids: prompts.map(p => p.id),
        total_prompts_found: prompts.length
      } 
    }];
  }

  // Return the selected prompt data in easy-to-use fields
  return [{ 
    json: { 
      selected_prompt: selectedPrompt.prompt,
      job_role: selectedPrompt.job_role,
      content_type: selectedPrompt.content_type,
      prompt_title: selectedPrompt.title,
      auto_id: autoId,
      total_prompts_available: prompts.length
    } 
  }];

} catch (error) {
  return [{ 
    json: { 
      error: `Failed to parse JSONL: ${error.message}`,
      rawContent: textContent.substring(0, 200) + "..."
    } 
  }];
}

